sys_ux_client_script.script:
/**
* @param {params} params
* @param {api} params.api
* @param {any} params.event
* @param {any} params.imports
* @param {ApiHelpers} params.helpers
*/
function handler({api, event, helpers, imports}) {
    console.log('Radio Button clicked');
    console.log('Client State params: (api.state)',api.state);
    console.log('List query:',api.state.listQuery);
    console.log('Event.payload',event.payload);
    var newListTitle = null;
    newListTitle = api.state.listTitle;
    newListTitle += event.payload.value;
    console.log('New List title',newListTitle);
    api.setState('listTitle',newListTitle);
}
